package com.example.batikapp.helperimport android.content.Contextimport android.content.SharedPreferencesclass SharedPreference(context: Context) {    companion object {        private const val SP_NAME = "com.example.batikapp"        private const val TOKEN_FIREBASE = "token_firebase"        private const val SUBSCRIBE_TOPIC = "subscribe_topic"//        private const val KAIN_1 = "kain_1"//        private const val KAIN_2 = "kain_2"//        private const val KAIN_3 = "kain_3"//        private const val KAIN_4 = "kain_4"//        private const val KAIN_5 = "kain_5"    }    private val preference: SharedPreferences =        context.getSharedPreferences(SP_NAME, Context.MODE_PRIVATE)    private val editor: SharedPreferences.Editor = preference.edit()    fun saveTokenFirebase(tokenFirebase: String) {        editor.putString(TOKEN_FIREBASE, tokenFirebase)        editor.apply()    }//    fun  saveKain1(isShow:Boolean){//        editor.putBoolean(KAIN_1, isShow)//        editor.apply()//    }    fun saveStateSubscribe(isSubscribe: Boolean) {        editor.putBoolean(SUBSCRIBE_TOPIC, isSubscribe)        editor.apply()    }    fun getTokenFirebase(): String {        return preference.getString(TOKEN_FIREBASE, "").toString()    }    fun getStateSubscribe(): Boolean {        return preference.getBoolean(SUBSCRIBE_TOPIC, false)    }//    fun getKain1(): Boolean {//        return preference.getBoolean(KAIN_1, false)//    }    fun clearPreference() {        editor.remove(TOKEN_FIREBASE)        editor.remove(TOKEN_FIREBASE)        editor.apply()    }}